import React, { useState, useEffect } from 'react';
import { Box, Center, SimpleGrid, Spinner } from '@chakra-ui/react';
import CardInfo from '../components/CardInfo';
import { api } from '../api';

interface UserData {
  email: string;
  password: string;
  name: string;
  balance: number;
}

const Conta = () => {
  const [userData, setUserData] = useState<null | UserData>(null);

  useEffect(() => {
    const getData = async () => {
      const data: any | UserData = await api;
      setUserData(data);
    };
    getData();
  }, []);

  const actualData = new Date();

  return (
    <Center>
      <SimpleGrid columns={2} spacing={8} paddingTop={16}>
        {userData === undefined || userData === null ? (
            <Spinner size='xl' color='white'/>
        )
        : 
        (
        <>
        <CardInfo mainContent={`Bem vindo! ${userData?.name}`}  content={`${actualData.getDay()} / ${actualData.getMonth()} / ${actualData.getFullYear()} ${actualData.getHours()}:${actualData.getMinutes()}`}/>)
        <CardInfo mainContent="Saldo" content={userData?.balance.toString}/>
        </>)
        }
      </SimpleGrid>
    </Center>
  );
};

export default Conta;
