import { createContext } from "vm";

interface IAppContext {
    user: string;
  }
  export const AppContext = createContext({} as IAppContext)

  const AppContextProvider = ({ children }: any) => {

    const user = 'wesley';

    return (
      <AppContext.Provider value={{ user }}>
        {children}
      </AppContext.Provider>
    )
  }
