import { Box, Center, SimpleGrid } from '@chakra-ui/react'
import React from 'react'

interface ICardInfo {
    text:string
}

const CardInfo = ({id,text}: ICardInfo) => {
  return (
    <Center>
      <SimpleGrid columns={2} spacing={8} paddingTop={16}>
        <Box backgroundColor='white'  minHeight='120px' padding={8}>
          {text}
        </Box>
      </SimpleGrid>
    </Center>
  )
}

export default CardInfo
