  id: number,
  paragraph: string,
  details: string
}

export const Card = ({ id, paragraph, details }: ICard) => {
  return(
    <ChakraProvider>
        <Box minHeight='100vh' backgroundColor='#9413dc' padding='25px'>
      <h1>Card {id}</h1>
      <p>{paragraph}</p>
      <p>{details}</p>
          <Box backgroundColor='#FFFFFF' borderRadius='25px' padding='15px' >
            <Center>
              <h1>Fa√ßa o login</h1>
            </Center>
